def makePortal(mapId = '', mapType = '', x = 0, y = 0):
    if not mapId:
        mapId = getEmptyMapId(mapType)
    portal = Entity({'class':'portal', 'targetMap':mapId, 'tx':x, 'ty':y})
    return portal

_return = makePortal(mapId, mapType, x, y)

def usePortal(portal, subject):
    mapId = portal.attr('targetMap')
    newMap = getMap(mapId)
    tx, ty = portal.attr('tx'), portal.attr('ty')
    if tx < 0 or ty < 0:
        tx, ty = newMap.attr('centre')
    return action('travel', {'subject':subject, 'mapId':mapId, 'x':tx, 'y':ty})

_return = usePortal(portal, subject)
