def makeRat(ratType):
    if not ratType:
        ratType = choice(['white', 'grey', 'toxic'])
    rat = Entity({
                'class':'rat',
                'ratType':ratType,
                'strength':2,
                'agility':2,
                'hungry':0, 'maxHungry':5
                })
    return rat

